<?phpnamespace c;use m\M_Users;class C_Auth extends C_Base{	public function __construct(){		parent::__construct();	}    /**     * Функция отрабатывающая до основного метода     * @return mixed|void     */	public function before(){		parent::before();	}    /**     * Экшн для авторизации     */	public function action_login()	{		$this->title .= '::Авторизация';		$mUsers = M_Users::Instance();		if($this->isPost())		{			if($userId = $mUsers->Login($_POST['username'], $_POST['password'])){				$_SESSION['uid'] = $userId;				$this->redirect('/');			}		}		$this->content = $this->Template('v/v_login.php');	}    /**     * Экшн для выхода     */	public function action_logout()	{		$this->user = NULL;		session_destroy();		$this->redirect('/auth/login');	}}