<?phpclass C_Auth extends C_Base{	public function __construct(){		parent::__construct();	}		public function before(){		parent::before();	}		public function action_login()	{		$this->title .= '::Авторизация';		$mUsers = M_Users::Instance();		if($this->isPost())		{			if($userId = $mUsers->Login($_POST['username'], $_POST['password'])){				$_SESSION['uid'] = $userId;				$this->redirect('/');			}		}		$this->content = $this->Template('v/v_login.php');	}	public function action_logout()	{		$this->user = NULL;		session_destroy();		$this->redirect('/auth/login');	}}