<?phpnamespace c;use c\C_Controller;/** * Базовый контроллер сайта * @property string $title // заголовок страницы * @property string $content // содержание страницы * @property boolean $needLogin // необходима ли авторизация * @property string $user // авторизованный пользователь || null */abstract class C_Base extends C_Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы	protected $needLogin;	// необходима ли авторизация	protected $user;		// авторизованный пользователь || null	function __construct()	{			$this->needLogin = false;	}    /**     * Функция отрабатывающая до основного метода     * @return mixed|void     */	protected function before()	{		$this->user = (isset($_SESSION['uid'])) ? $_SESSION['uid'] : NULL;		if($this->needLogin && $this->user === null){			$this->redirect('/auth/login');		}			$this->title = 'Тестовое задание';		$this->content = '';	}    /**     * Генерация базового шаблона     * @return mixed|void     */	public function render()	{		$vars = array('title' => $this->title, 'content' => $this->content);			$page = $this->Template('v/v_main.php', $vars);						echo $page;	}	}