<?php//// Базовый контроллер сайта.//abstract class C_Base extends C_Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы	protected $needLogin;	// необходима ли авторизация	protected $user;		// авторизованный пользователь || null	//	// Конструктор.	//	function __construct()	{			$this->needLogin = false;	}		protected function before()	{		$this->user = (isset($_SESSION['uid'])) ? $_SESSION['uid'] : NULL;		if($this->needLogin && $this->user === null){			$this->redirect('/auth/login');		}			$this->title = 'Тестовое задание';		$this->content = '';	}			//	// Генерация базового шаблонаы	//		public function render()	{		$vars = array('title' => $this->title, 'content' => $this->content);			$page = $this->Template('v/v_main.php', $vars);						echo $page;	}	}