<?phpnamespace c;use m\M_Params;use m\M_PDO;class C_Handler extends C_Base{    protected function before()    {    }    /**     * Добавление случайного круга     */    public function action_create()    {        $user_id = (isset($_POST['uid'])) ? $_POST['uid'] : NULL;        if (!!$user_id) {            $params = M_Params::Instance()->randomParams();            $circle = M_Params::Instance()->randomCircle($params);            $params['user_id'] = $user_id;            $result = M_PDO::Instance()->Insert('circles', $params);        }        echo (preg_match("/[0-9]+/", $result)) ? $circle : NULL;    }    public function action_delete()    {        $user_id = (isset($_POST['uid'])) ? $_POST['uid'] : NULL;        $result = 0;        if (!!$user_id) {            $where = 'user_id =' . $user_id;            $result = M_PDO::Instance()->Delete('circles', $where);        }        echo $result !== 0 ? 'deleted' : $result;    }    public function action_count()    {    }}