<?phpclass C_Index extends C_Base{	public function __construct()	{		parent::__construct();	}		public function before()	{		$this->needLogin = true;		parent::before();	}		public function action_index()	{		$dataInstance = M_Params::Instance();		$circles = $dataInstance->getMap($this->user);		$playField = new M_Figure(BG_SIDE, BG_SIDE, BG_BORDER, BG_COLOR);		$canvas = $playField->generateTag('content-images');		$this->title .= '';		$this->content = $this->Template('v/v_index.php', array('id' => $this->user, 'canvas' => $canvas, 'circles' => $circles));	}}