<?phpnamespace c;use m\M_Params;use m\M_Figure;use m\M_Tree;class C_Index extends C_Base{	public function __construct()	{		parent::__construct();	}    /**     * Функция отрабатывающая до основного метода     * @return mixed|void     */	public function before()	{		$this->needLogin = true;		parent::before();	}    /**     * Экшн для главной страницы     */	public function action_index()	{		$dataInstance = M_Params::Instance();		$circles = $dataInstance->getMap($this->user);		$playField = new M_Figure(BG_SIDE, BG_SIDE, BG_BORDER, BG_COLOR);		$canvas = $playField->generateTag('content-images',$circles);		$array = array(			array(				'id' => 1,				'parent_id'=>0,				'content' => 'Content1'			),			array(				'id' => 2,				'parent_id'=>1,				'content' => 'Content2'			),			array(				'id' => 3,				'parent_id'=>1,				'content' => 'Content3'			),			array(				'id' => 4,				'parent_id'=>2,				'content' => 'Content4'			),			array(				'id' => 5,				'parent_id'=>4,				'content' => 'Content5'			),			array(				'id' => 6,				'parent_id'=>0,				'content' => 'Content6'			)		);		$tree = new M_Tree($array);		$tree->setColumnsNames(['id', 'parent_id', ['content']]);		$tree->getTreeStructure();		$list =  $tree->formList();		$this->title .= '';		$this->content = $this->Template('v/v_index.php', array('id' => $this->user, 'canvas' => $canvas, 'list' => $list));	}}