<?phpfunction __autoload($classname){	switch($classname[0])	{		case 'C':			if (file_exists("c/$classname.php")) {				include_once("c/$classname.php");			}			break;		case 'M':			if (file_exists("m/$classname.php")) {				include_once("m/$classname.php");			}			break;	}}define('BASE_URL', '/');define('MYSQL_SERVER', 'localhost');define('MYSQL_USER', 'root');define('MYSQL_PASSWORD', 'root');define('MYSQL_DB', 'webberry');define('BG_SIDE', 500);define('BG_BORDER', '2px solid #000');define('BG_COLOR', '#ccc');define ('RADIUS_MIN', 10);define ('RADIUS_MAX', 40);define ('COLOR_MAX', 16777215);